insert into mysql.db select Host , Db                 , User    , Select_priv , Insert_priv , Update_priv , Delete_priv , Create_priv , Drop_priv , Grant_priv , References_priv , Index_priv , Alter_priv , Create_tmp_table_priv , Lock_tables_priv , Create_view_priv , Show_view_priv , Create_routine_priv , Alter_routine_priv , Execute_priv , Event_priv , Trigger_priv from provisioning.db_delta pdu where NOT EXISTS (select 1 from mysql.db t where t.user=pdu.user and t.host=pdu.host) AND Server = concat(SUBSTRING(@@hostname,1,INSTR(@@hostname,'.')-1),'_',@@port);
