update mysql.db du, provisioning.db_delta pdu set du.Select_priv=pdu.Select_priv , du.Insert_priv=pdu.Insert_priv , du.Update_priv=pdu.Update_priv , du.Delete_priv=pdu.Delete_priv , du.Create_priv=pdu.Create_priv , du.Drop_priv=pdu.Drop_priv , du.Grant_priv=pdu.Grant_priv , du.References_priv=pdu.References_priv , du.Index_priv=pdu.Index_priv , du.Alter_priv=pdu.Alter_priv  , du.Create_tmp_table_priv=pdu.Create_tmp_table_priv , du.Lock_tables_priv=pdu.Lock_tables_priv, du.Create_view_priv=pdu.Create_view_priv , du.Show_view_priv=pdu.Show_view_priv , du.Create_routine_priv=pdu.Create_routine_priv , du.Alter_routine_priv=pdu.Alter_routine_priv , du.Execute_priv=pdu.Execute_priv , du.Event_priv=pdu.Event_priv , du.Trigger_priv=pdu.Trigger_priv where du.host=pdu.host AND du.user=pdu.user AND du.user NOT IN ('DBAS_GO_HERE') AND Server = concat(SUBSTRING(@@hostname,1,INSTR(@@hostname,'.')-1),'_',@@port);
